import React from 'react';
import './Input.css';

const Input = ({ label, type = 'text', value, onChange, placeholder, hint, error }) => {
    return (
        <div className="form_group">
            {label && <label className="input_label">{label}</label>}
            <input
                type={type}
                value={value}
                onChange={onChange}
                placeholder={placeholder}
                className={`input_field ${error ? 'input_error' : ''}`}
            />
            {hint && <span className={`input_hint ${error ? 'error_hint' : ''}`}>{hint}</span>}
        </div>
    );
};

export default Input;
